mapper.update:
UPDATE sys_window
NAME = #{name,jdbcType=VARCHAR}, ICON = #{icon,jdbcType=VARCHAR}, MODEL = #{model,jdbcType=VARCHAR}, URL = #{url,jdbcType=VARCHAR}, PARENT_ID = #{parentWindow.id,jdbcType=INTEGER}, IS_VALID = #{isValid,jdbcType=INTEGER}, IS_SHOW = #{isShow,jdbcType=INTEGER}, IS_LEAF = #{isLeaf,jdbcType=INTEGER}, EXPANDED = #{expanded,jdbcType=VARCHAR}, REMARK = #{remark,jdbcType=VARCHAR}
WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:
SELECT ID, NAME, EXPANDED, ICON, IS_LEAF FROM sys_window WHERE IS_SHOW = 1 AND IS_VALID = 1
AND PARENT_ID IS NULL
AND PARENT_ID = #{windowId,jdbcType=INTEGER}
